update sppi_questions set content='Является ли подверженность кредитному риску, присущая траншу, такой же или меньшей по сравнению с подверженностью кредитному риску, присущей базовому пулу финансовых инструментов?' where code in ('CLE3.6.1','CLE3.6.2') ;
